import React, { useEffect, useRef } from "react";
import { GoogleMap, Marker } from 'react-google-maps';
import loadScript from 'load-script-promise';


export default function Contact() {
    const formsRef = useRef(null);
    const [mapsLoaded, setMapsLoaded] = useState(false);
    if (!mapsLoaded) {
        return <div>Loading...</div>;
    }
    const mapOptions = {
        center: {
            lat: 40.044438,
            lng: -6.125672
        },
        zoom: 15,
        styles: [
            {
                "featureType": "water",
                "elementType": "geometry",
                "stylers": [
                    {
                        "color": "#a2daf2"
                    }
                ]
            },
            {
                "featureType": "landscape",
                "elementType": "geometry",
                "stylers": [
                    {
                        "color": "#f2f2f2"
                    }
                ]
            },
            {
                "featureType": "road",
                "elementType": "geometry",
                "stylers": [
                    {
                        "color": "#ffffff"
                    }
                ]
            },
            {
                "featureType": "poi",
                "elementType": "geometry",
                "stylers": [
                    {
                        "color": "#eeeeee"
                    }
                ]
            },
            {
                "featureType": "administrative",
                "elementType": "geometry",
                "stylers": [
                    {
                        "color": "#fefefe"
                    },
                    {
                        "visibility": "on"
                    }
                ]
            },
            {
                "featureType": "road",
                "elementType": "labels",
                "stylers": [
                    {
                        "visibility": "off"
                    }
                ]
            },
            {
                "featureType": "poi",
                "elementType": "labels",
                "stylers": [
                    {
                        "visibility": "off"
                    }
                ]
            },
            {
                "featureType": "transit",
                "elementType": "labels",
                "stylers": [
                    {
                        "visibility": "off"
                    }
                ]
            },
            {
                "featureType": "landscape.man_made",
                "elementType": "geometry",
                "stylers": [
                    {
                        "color": "#ffffff"
                    }
                ]
            }
        ]
    };

    const markerData = [
        {
            location: {
                lat: 40.044438,
                lng: -6.125672
            },
            description: 'Amwell St, Islington LONDON EC1R 1UR United Kingdom'
        }
    ];
    useEffect(() => {
        const loadMapScript = async () => {
            try {
                await loadScript(`https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY`);
                setMapsLoaded(true);
            } catch (error) {
                console.error('Error loading Google Maps script:', error);
            }
        };

        loadMapScript();
        const forms = formsRef.current;
        const script = document.createElement('script');
        script.src = 'https://www.google.com/recaptcha/api.js';
        script.async = true;
        script.defer = true;
        document.body.appendChild(script);
        function displayError(thisForm, error) {
            thisForm.querySelector('.loading').classList.remove('d-block');
            thisForm.querySelector('.error-message').innerHTML = error;
            thisForm.querySelector('.error-message').classList.add('d-block');
        }
        function php_email_form_submit(thisForm, action, formData) {
            fetch(action, {
                method: 'POST',
                body: formData,
                headers: { 'X-Requested-With': 'XMLHttpRequest' }
            })
                .then(response => {
                    if (response.ok) {
                        return response.text()
                    } else {
                        throw new Error(`${response.status} ${response.statusText} ${response.url}`);
                    }
                })
                .then(data => {
                    thisForm.querySelector('.loading').classList.remove('d-block');
                    if (data.trim() === 'OK') {
                        thisForm.querySelector('.sent-message').classList.add('d-block');
                        thisForm.reset();
                    } else {
                        throw new Error(data ? data : 'Form submission failed and no error message returned from: ' + action);
                    }
                })
                .catch((error) => {
                    displayError(thisForm, error);
                });
        }

        if (!forms) {
            return;
        }

        forms.forEach(function (e) {
            e.addEventListener('submit', function (event) {
                event.preventDefault();

                let thisForm = this;

                let action = thisForm.getAttribute('action');

                if (!action) {
                    displayError(thisForm, 'The form action property is not set!');
                    return;
                }
                thisForm.querySelector('.loading').classList.add('d-block');
                thisForm.querySelector('.error-message').classList.remove('d-block');
                thisForm.querySelector('.sent-message').classList.remove('d-block');

                let formData = new FormData(thisForm);

                php_email_form_submit(thisForm, action, formData);
            });
        });
        const loadMap = () => {
            // Load the Google Maps API script dynamically
            const script = document.createElement('script');
            script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyCyNfiJB1Wuewp4A4teb0N5rubFiXaYn0w&callback=initMap`;
            script.async = true;
            script.defer = true;
            document.head.appendChild(script);

            // Initialize the map
            window.initMap = () => {
            const mapContainer = document.querySelector('.google-map');
            const center = {
                lat: 40.044438,
                lng: -6.125672
            };
            const mapOptions = {
                center: center,
                zoom: 15,
                styles: [
                    {
                        "featureType": "water",
                        "elementType": "geometry",
                        "stylers": [
                        {
                            "color": "#a2daf2"
                        }
                        ]
                    },
                    {
                        "featureType": "landscape",
                        "elementType": "geometry",
                        "stylers": [
                        {
                            "color": "#f2f2f2"
                        }
                        ]
                    },
                    {
                        "featureType": "road",
                        "elementType": "geometry",
                        "stylers": [
                        {
                            "color": "#ffffff"
                        }
                        ]
                    },
                    {
                        "featureType": "poi",
                        "elementType": "geometry",
                        "stylers": [
                        {
                            "color": "#eeeeee"
                        }
                        ]
                    },
                    {
                        "featureType": "administrative",
                        "elementType": "geometry",
                        "stylers": [
                        {
                            "color": "#fefefe"
                        },
                        {
                            "visibility": "on"
                        }
                        ]
                    },
                    {
                        "featureType": "road",
                        "elementType": "labels",
                        "stylers": [
